    <script type="text/javascript">
        // Initialize dates
        window.addEventListener('DOMContentLoaded', function() {
            const today = new Date();
            const threeMonthsAgo = new Date();
            threeMonthsAgo.setMonth(today.getMonth() - 3);

            document.getElementById('endDate').value = today.toISOString().split('T')[0];
            document.getElementById('startDate').value = threeMonthsAgo.toISOString().split('T')[0];
        });

        // Setup callback to receive data from MATLAB
        function setup(htmlComponent) {
            htmlComponent.addEventListener("DataChanged", function(event) {
                handleMATLABData(event.Data);
            });
        }

        function selectCity(city) {
            const data = {
                action: 'quickSelect',
                city: city
            };
            sendToMATLAB(data);
        }

        function fetchData() {
            const latitude = parseFloat(document.getElementById('latitude').value);
            const longitude = parseFloat(document.getElementById('longitude').value);
            const startDate = document.getElementById('startDate').value;
            const endDate = document.getElementById('endDate').value;

            if (!latitude || !longitude || !startDate || !endDate) {
                showStatus('Please fill in all fields', 'error');
                return;
            }

            const data = {
                action: 'fetchData',
                latitude: latitude,
                longitude: longitude,
                startDate: startDate,
                endDate: endDate
            };

            sendToMATLAB(data);
        }

        function performRegression() {
            const data = {
                action: 'performRegression'
            };
            sendToMATLAB(data);
        }

        function sendToMATLAB(data) {
            if (typeof htmlComponent !== 'undefined') {
                htmlComponent.Data = data;
            }
        }

        function handleMATLABData(data) {
            if (!data || !data.action) return;

            switch(data.action) {
                case 'status':
                    showStatus(data.message, data.type);
                    break;

                case 'updateLocation':
                    document.getElementById('latitude').value = data.latitude;
                    document.getElementById('longitude').value = data.longitude;
                    break;

                case 'displayData':
                    displayWeatherData(data);
                    break;

                case 'displayRegression':
                    displayRegressionResults(data);
                    break;
            }
        }

        function displayWeatherData(data) {
            document.getElementById('dataSection').classList.add('visible');
            document.getElementById('regressionBtn').disabled = false;

            document.getElementById('dataPoints').textContent = data.dataPoints;

            const avgTemp = (data.tempAvg.reduce((a, b) => a + b, 0) / data.tempAvg.length).toFixed(1);
            const maxTemp = Math.max(...data.tempMax).toFixed(1);
            const minTemp = Math.min(...data.tempMin).toFixed(1);

            document.getElementById('avgTemp').textContent = avgTemp;
            document.getElementById('maxTemp').textContent = maxTemp;
            document.getElementById('minTemp').textContent = minTemp;
        }

        function displayRegressionResults(data) {
            document.getElementById('regressionSection').classList.add('visible');

            const slope = data.slope.toFixed(6);
            const intercept = data.intercept.toFixed(2);
            const rSquared = data.rSquared.toFixed(4);
            const rmse = data.rmse.toFixed(2);

            document.getElementById('slope').textContent = slope;
            document.getElementById('intercept').textContent = intercept;
            document.getElementById('rSquared').textContent = rSquared;
            document.getElementById('rmse').textContent = rmse + ' C';

            document.getElementById('equation').textContent = 
                `Temperature = ${slope}  Day + ${intercept}`;

            document.getElementById('trendText').textContent = data.trendText;

            const tempChange = data.tempChange.toFixed(2);
            const changeText = tempChange > 0 ? 
                `Temperature increased by ${tempChange}C over the period` :
                `Temperature decreased by ${Math.abs(tempChange)}C over the period`;
            document.getElementById('tempChange').textContent = changeText;
        }

        function showStatus(message, type) {
            const statusDiv = document.getElementById('status');
            statusDiv.textContent = message;
            statusDiv.className = 'status ' + type;
        }
    </script>
</body>
</html>
